mapper.update:
UPDATE ke_org_info
SET org_id=#{orgId}
WHERE id=#{id}

UPDATE ke_teacher
SET org_id=#{orgId}

    , teacher_name=#{teaName}


    , subject_id='02'


    , grade=#{teaGrade}


    , password=#{password}

WHERE teacher_id=#{teacherId}

UPDATE ke_org_info
SET password=#{password},update_time=now()
WHERE phone_num=#{phoneNumber}

mapper.select:
SELECT CONCAT('U',a.id) id, a.user_id userId, a.user_name name, a.grade grade, a.phone_number phoneNumber, a.user_level stuLevel, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, b.city_name cityName, c.city_name provinceName, a.type type, a.on_type onType, '0' userType FROM ke_user a LEFT JOIN ke_city b ON a.city_code=b.city_code LEFT JOIN ke_city c ON a.province_code=c.city_code WHERE a.org_id=#{orgId}
AND a.grade=#{grade}
limit #{page},#{size}

mapper.update:
        UPDATE ke_user
		SET grade=#{gradetwo},update_time=NOW()
		WHERE grade=#{gradeone}
		AND org_id=#{orgId}

mapper.select:
SELECT count(1)
FROM ke_user
WHERE org_id=#{orgId}
AND type = #{type}
